export const initialState = {
    todos: [
        id
    ],
};

const reducer = (state, action) =>{
    console.log(action);

    switch(action.type){ 
        case "ADD_TODO": 
        return {
            ...state,
        };

        default:
            return {
                ...state,
            };
    }
};

export default reducer;
