export const initialState = {
    todos: [
    {
        id: "1", 
        content: 'Ben İlk yapılacak işim',
   },
],
};

const reducer = (state, action) =>{
    console.log(action);

    switch(action.type){ 
        case "Ekle_TODO": 
        return {
            ...state,
            todos:[action.payload, ...state.todos],
        };

        case "Kaldir_TODO": 
        return {
            ...state,
            todos:[action.payload, ...state.todos],
        };

        default:
            return {
                ...state,
            };
    }
};

export default reducer;
