export const initialState = {
    todos: [
        {id: "1", 
        content: 'Ben İlk yapılacak işim',
   } ],
};

const reducer = (state, action) =>{
    console.log(action);

    switch(action.type){ 
        case "ADD_TODO": 
        return {
            ...state,
        };

        default:
            return {
                ...state,
            };
    }
};

export default reducer;
