    const [{ }, dispatch] = useTodoLayerValue();

    const removeTodo = todoid=>{
      dispatch({
        type: 'REMOVE_TODO',
        pay
      })
    }
  return (
    <div className="todo-row">
      <div>
        {todo.content}
      </div>
      <div className="todo-icons">
        <GrFormClose className="todo-icon" onclick={()=>removeTodo(todo.id)} />
        <GrFormEdit className="todo-icon" />
      </div>
    </div>
  )
}

export default Todo
